mapper.select:
select id, username, password, create_time as createTime, update_time as updateTime, forbiden, is_delete as isDelete, male, age, birthday, address, email, telephone FROM yx_admin_user
and username LIKE '%${keyword}%'
and is_delete = #{isDelete}
ORDER BY ${order} LIMIT #{pageNo} ,#{pageSize};

SELECT count(1) FROM yx_admin_user
and username LIKE '%${keyword}%'
and is_delete = #{isDelete}

mapper.delete:
update yx_admin_user
forbiden=#{forbiden}, is_delete=#{isDelete},
where id = #{id}
