mapper.select:
select yp.id, yp.name, yp.description, yp.create_time as createTime, yp.url, yp.model, yu.email as adminUserName, yp.parent_id as parentId, yp.method, yp.user_id as userId, yp.is_delete as isDelete FROM yx_permission as yp left JOIN yx_admin_user as yu ON yp.user_id = yu.id
and url LIKE '%${keyword}%'
order by ${order} LIMIT #{pageNo} ,#{pageSize}
