mapper.select:
select id, name, create_time as createTime, update_time as updateTime, user_id as userId, is_delete as isDelete, description FROM yx_role
and name LIKE '%${keyword}%'
ORDER BY ${order} LIMIT #{pageNo} ,#{pageSize};

SELECT id,username,email FROM yx_admin_user WHERE id NOT IN(
SELECT user_id FROM yx_role_user WHERE role_id =#{roleId})

	and username LIKE '%${keyword}%'


	and is_delete = #{isDelete} 

LIMIT #{pageNo} ,#{pageSize};

SELECT count(1) FROM yx_admin_user WHERE id NOT IN( SELECT user_id FROM yx_role_user WHERE role_id =#{roleId})
and username LIKE '%${keyword}%'
and is_delete = #{isDelete}

SELECT GROUP_CONCAT(`name`) as name,CONVERT(GROUP_CONCAT(id),CHARACTER) as rids FROM yx_role_user  ru
LEFT JOIN yx_role r ON r.id = ru.role_id
WHERE ru.user_id =#{userId}
